async def update_country_providers(
    executor: edgedb.AsyncIOExecutor,
    *,
    providers: str,
    country_code: str,
) -> list[InsertCountriesResult]:
    return await executor.query(
        """\
        with
          providers := <json>$providers
        for provider in json_array_unpack(providers) union (
          update Country
          filter .value = <str>$country_code
          set {
            providers := (
              select detached Provider {
                @display_priority := <int16>provider["display_priority"]
              }
              filter .provider_id = <int16>provider["provider_id"]
            )
          }
        )\
        """,
        providers=providers,
        country_code=country_code,
    )


async def update_user_watch_list_add(
    executor: edgedb.AsyncIOExecutor,
    *,
    email: str,
    streamchaser_id: str,
) -> InsertUserResult | None:
    return await executor.query_single(
        """\
        update User
        filter .email = <str>$email
        set {
          watch_list += (
            insert Media { streamchaser_id := <str>$streamchaser_id }
            unless conflict on .streamchaser_id
            else (
              select Media filter .streamchaser_id = <str>$streamchaser_id
            )
          )
        };\
        """,
        email=email,
        streamchaser_id=streamchaser_id,
    )


async def update_user_watch_list_remove(
    executor: edgedb.AsyncIOExecutor,
    *,
    email: str,
    streamchaser_id: str,
) -> InsertUserResult | None:
    return await executor.query_single(
        """\
        update User
        filter .email = <str>$email
        set {
          watch_list -= (
            select Media filter .streamchaser_id = <str>$streamchaser_id
          )
        };\
        """,
        email=email,
        streamchaser_id=streamchaser_id,
    )
